import React, { Component } from 'react';
import Modal from './Modal';

export class Item extends Component {
  state = {
    isModalOpen: false
  };

  handleDismissClick = () => {
    this.setState({ isModalOpen: true });
  };

  handleModalClose = () => {
    this.setState({ isModalOpen: false });
  };

  handleModalSubmit = reason => {
    this.setState({ isModalOpen: false });
    this.props.onDismiss(reason);
  };

  render() {
    return (
      <div className="item">
        <img src={"./img/" + this.props.item.img} />
        <h2>{this.props.item.title}</h2>
        <p>{this.props.item.desc}</p>
        <b>{this.props.item.reason}</b>
        <div className="add-to-dismissal" onClick={this.handleDismissClick}>
          +
        </div>
        {this.state.isModalOpen && (
          <Modal onDismiss={this.handleModalSubmit} onClose={this.handleModalClose} />
        )}
      </div>
    );
  }
}

export default Item;
