import React, { Component } from 'react';

class DismissalModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      reason: ''
    };
  }

  handleReasonChange = (event) => {
    this.setState({ reason: event.target.value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.onDismiss(this.state.reason);
    this.setState({ reason: '' });
  }

  render() {
    return (
      <div className="modal">
        <div className="modal-content">
          <h2>Enter Dismissal Reason</h2>
          <form onSubmit={this.handleSubmit}>
            <label>
              Reason:
              <input type="text" value={this.state.reason} onChange={this.handleReasonChange} />
            </label>
            <input type="submit" value="Уволить" />
          </form>
        </div>
      </div>
    );
  }
}

export default DismissalModal;
