import React, { Component } from 'react'

class Dismissal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      reason: ''
    }
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    this.setState({reason: event.target.value});
  }

  handleSubmit(event) {
    alert('Причина увольнения: ' + this.state.reason);
    event.preventDefault();
  }

  render() {
    return (
      <div className="modal">
        <div className="modal-content">
          <h2>Увольнение сотрудника</h2>
          <form onSubmit={this.handleSubmit}>
            <label>
              Причина увольнения:
              <input type="text" value={this.state.reason} onChange={this.handleInputChange} />
            </label>
            <input type="submit" value="Уволить" />
          </form>
          <button onClick={this.props.onClose}>Отмена</button>
        </div>
      </div>
    );
  }
}

export default Dismissal;
