import React, { Component } from 'react';
import Dismissal from './Dismissal';

export class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      reason: '',
    };
  }

  handleClick = () => {
    this.setState({
      showModal: true,
    });
  };

  handleInputChange = (event) => {
    this.setState({
      reason: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // Do something with the reason (e.g. save it to the database)
    console.log(this.state.reason);
    // Close the modal
    this.setState({
      showModal: false,
      reason: '',
    });
  };

  render() {
    return (
      <div className='item'>
        <img src={"./img/" + this.props.item.img} />
        <h2>{this.props.item.title}</h2>
        <p>{this.props.item.desc}</p>
        <b>{this.props.item.reason}</b>
        <div className='add-to-dismissal' onClick={this.handleClick}>+</div>
        {this.state.showModal && (
          <Modal>
            <form onSubmit={this.handleSubmit}>
              <label>
                Причина увольнения:
                <input type='text' value={this.state.reason} onChange={this.handleInputChange} />
              </label>
              <button type='submit'>Отправить</button>
            </form>
          </Modal>
        )}
      </div>
    );
  }
}

export default Item;
