import React, { useState } from 'react';

function DismissalForm() {
  const [showModal, setShowModal] = useState(false);
  const [reason, setReason] = useState('');

  const handleInputChange = (e) => {
    setReason(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // handle form submission
    console.log('Reason for termination:', reason);
    setShowModal(false);
  };

  return (
    <div>
      <button onClick={() => setShowModal(true)}>Нажмите, чтобы ввести причину увольнения</button>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowModal(false)}>&times;</span>
            <form onSubmit={handleSubmit}>
              <label htmlFor="reason">Причина увольнения:</label>
              <input
                id="reason"
                type="text"
                value={reason}
                onChange={handleInputChange}
                required
              />
              <button type="submit">Отправить</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dismissal;
