import React, { Component } from 'react';
import Modal from './Modal';

export class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isModalOpen: false,
      dismissalReason: '',
    };
  }

  handleAddToDismissal = () => {
    this.setState({
      isModalOpen: true,
    });
  };

  handleModalClose = () => {
    this.setState({
      isModalOpen: false,
      dismissalReason: '',
    });
  };

  handleDismissalReasonChange = (event) => {
    this.setState({
      dismissalReason: event.target.value,
    });
  };

  handleDismissalSubmit = () => {
    // Do something with the dismissal reason, e.g. send it to the server
    console.log(`Dismissal reason: ${this.state.dismissalReason}`);
    this.handleModalClose();
  };

  render() {
    return (
      <div className='item'>
        <img src={'./img/' + this.props.item.img} />
        <h2>{this.props.item.title}</h2>
        <p>{this.props.item.desc}</p>
        <b>{this.props.item.reason}</b>
        <div className='add-to-dismissal' onClick={this.handleAddToDismissal}>
          +
        </div>
        <Modal isOpen={this.state.isModalOpen} onClose={this.handleModalClose}>
          <h2>Enter dismissal reason</h2>
          <input
            type='text'
            value={this.state.dismissalReason}
            onChange={this.handleDismissalReasonChange}
          />
          <button onClick={this.handleDismissalSubmit}>Submit</button>
        </Modal>
      </div>
    );
  }
}

export default Item;
