import React, { Component } from 'react';
import Modal from './Modal';

export class Item extends Component {
  state = {
    showModal: false,
    dismissalReason: '',
  };

  handleDismissalClick = () => {
    this.setState({
      showModal: true,
    });
  };

  handleDismissalReasonChange = (event) => {
    this.setState({
      dismissalReason: event.target.value,
    });
  };

  handleDismissalSubmit = () => {
    // Do something with the dismissal reason, such as send it to a server
    console.log(`Dismissal reason: ${this.state.dismissalReason}`);

    this.setState({
      showModal: false,
      dismissalReason: '',
    });
  };

  handleModalClose = () => {
    this.setState({
      showModal: false,
    });
  };

  render() {
    const { item } = this.props;

    return (
      <div className='item'>
        <img src={`./img/${item.img}`} alt={item.title} />
        <h2>{item.title}</h2>
        <p>{item.desc}</p>
        <b>{item.reason}</b>
        <div className='add-to-dismissal' onClick={this.handleDismissalClick}>
          +
        </div>
        {this.state.showModal && (
          <Modal onClose={this.handleModalClose}>
            <h3>Please enter a reason for dismissal</h3>
            <textarea
              value={this.state.dismissalReason}
              onChange={this.handleDismissalReasonChange}
            />
            <button onClick={this.handleDismissalSubmit}>Submit</button>
          </Modal>
        )}
      </div>
    );
  }
}

export default Item;
