import React from 'react';

class Dismissal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showDismissalForm: false,
      dismissalReason: ''
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleClick() {
    this.setState(prevState => ({
      showDismissalForm: !prevState.showDismissalForm
    }));
  }

  handleChange(event) {
    this.setState({ dismissalReason: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    // Do something with the dismissal reason, such as send it to a server or update the state of a parent component.
    console.log(this.state.dismissalReason);
    this.setState({ dismissalReason: '' });
    this.handleClick();
  }

  render() {
    return (
      <div>
        <button className='add-to-dismissal' onClick={this.handleClick}>+</button>
        {this.state.showDismissalForm &&
          <div className='dismissal-form'>
            <form onSubmit={this.handleSubmit}>
              <label>
                Причина увольнения:
                <input type='text' value={this.state.dismissalReason} onChange={this.handleChange} />
              </label>
              <input type='submit' value='Submit' />
            </form>
          </div>
        }
      </div>
    );
  }
}

export default Dismissal;
