import React, { Component } from 'react'
import Dismissal from './Dismissal'

class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showDismissalModal: false
    };
    this.handleDismissalClick = this.handleDismissalClick.bind(this);
    this.handleDismissalClose = this.handleDismissalClose.bind(this);
  }

  handleDismissalClick() {
    this.setState({ showDismissalModal: true });
  }

  handleDismissalClose() {
    this.setState({ showDismissalModal: false });
  }

  render() {
    return (
      <div className='item'>
        <img src={"./img/" + this.props.item.img} />
        <h2>{this.props.item.title}</h2>
        <p>{this.props.item.desc}</p>
        <b>{this.props.item.reason}</b>
        <div className='add-to-dismissal' onClick={this.handleDismissalClick}>+</div>
        {this.state.showDismissalModal &&
          <Dismissal onClose={this.handleDismissalClose} />
        }
      </div>
    )
  }
}

export default Item;
