import React, { Component } from 'react';
import DismissalModal from './DismissalModal';

export class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isModalOpen: false
    };
  }

  handleDismiss = (reason) => {
    console.log(`Dismissed item ${this.props.item.title} with reason: ${reason}`);
    this.setState({ isModalOpen: false });
  }

  handleAddToDismissal = () => {
    this.setState({ isModalOpen: true });
  }

  handleCloseModal = () => {
    this.setState({ isModalOpen: false });
  }

  render() {
    return (
      <div className='item'>
        <img src={"./img/" + this.props.item.img} />
        <h2>{this.props.item.title}</h2>
        <p>{this.props.item.desc}</p>
        <b>{this.props.item.reason}</b>
        <div className='add-to-dismissal' onClick={this.handleAddToDismissal}>+</div>
        {this.state.isModalOpen && (
          <DismissalModal onDismiss={this.handleDismiss} onClose={this.handleCloseModal} />
        )}
      </div>
    );
  }
}

export default Item;
